import React from 'react';
import {
    BodyContainer,
    Container,
    HeroContainer
} from '../StyledComponents/StyledComponents';
import Courses from '../../Courses';
import {
    ResumeTitle,
    ResumeItem,
} from '../../components/About/StyledComponents';


const CourseHero = () => {
    return (
        <>
                <HeroContainer
                    animate={{ opacity: 1 }}
                    initial={{ opacity: 0 }}
                    transition={{ duration: 2, delay: 1 }}
                    exit={{ opacity: 0 }}
                >
                    <BodyContainer>
                        <Container top={6}>
                            <Container width={50} bottom={6} right={2}>
                                <h1>Computer Science</h1>
                            </Container>
                            <Container width={50}>
                                {Courses.CS.map((course) => {
                                    return (
                                        <ResumeItem>
                                            <ResumeTitle>
                                                <span>
                                                    {course.title}
                                                </span>
                                            </ResumeTitle>
                                            <p>{course.description}</p>
                                        </ResumeItem>
                                    )})
                                }
                            </Container>
                        </Container>
                    </BodyContainer>

                </HeroContainer>
        </>
    );
}
export default CourseHero;
