import React from 'react';
import { Container,HeroContainer } from '../StyledComponents/StyledComponents';
import {
    ResumeTitle,
    ResumeItem,
} from '../../components/About/StyledComponents';

import courses from '../../Courses';
import Data from '../../Data';


const CourseHero = () => {
    return (
        <HeroContainer>
            <div>
                <Container width={50} right={3} bottom={3}>
                    <h1>Education</h1>
                </Container>
                <Container width={50}>
                    {Data.about.education.map((degree) => {
                        return (
                            <ResumeItem>
                                <ResumeTitle>
                                    <span>
                                        {degree.degree} in {degree.program}
                                    </span>
                                    <p>{degree.school}, {degree.date}</p>
                                </ResumeTitle>
                                <p>{degree.about}</p>
                            </ResumeItem>
                        )})
                    }
                </Container>
            </div>
            <Container flex bottom={6}>
                <Container width={50} right={3} bottom={3}>
                    <h1>Experience</h1>
                </Container>

                <Container width={50}>
                    {Data.about.experience.map((role) => {
                        return (
                            <ResumeItem>
                                <ResumeTitle>
                                    <span>{role.title + ' '}</span>
                                    <span>at </span>
                                    <span>{role.company}</span>
                                </ResumeTitle>
                                <p>{role.date}</p>
                                <p>{role.description}</p>
                            </ResumeItem>
                        )})
                    }
                </Container>
            </Container>
        </HeroContainer>
    );

}
export default CourseHero;
