import React from 'react';
import {
    Container,
    HeroContainer,
    BodyContainer
} from '../StyledComponents/StyledComponents';
import Data from '../../Data';

const AboutHero = () => {
    return(
        <HeroContainer
            animate={{ opacity: 1 }}
            initial={{ opacity: 0 }}
            transition={{ duration: 0.5, delay: 0.5 }}
            exit={{ opacity: 0 }}
            style={{ marginTop: '-10px', marginBottom: '-10px' }}
        >
            <h1>{Data.about.title}</h1>
        </HeroContainer>
    )
}

export default AboutHero;
